Use M3_MAIN set dateformat ymd SELECT CMAssess.cmr_name as AssessorName, COUNT(*) as cnt, sum((DSS_ApplicationFees) +  (DSS_TotalInterest) + (DSS_MonthlyServiceFee)) as 'Fees', sum((DSS_CashOut) +  (DSS_Insurance) + (DSS_Brokerage)) as 'NAF' FROM M3_MAIN.dbo.iO_DataStorage_SalesReport left JOIN iO_Product_MasterReference on DSS_LoanNumber = RMR_SeqNumber left join iO_Link_MasterReference  ON RMR_ID = LMR_IDLink_Code_ID left JOIN iO_Client_MasterReference CM ON CM.CMR_ID = LMR_IDLink_Code_ID left JOIN iO_Control_LinkMaster ON XLK_ID = LMR_IDLink_Association left JOIN iO_Client_MasterReference CMAssess ON CMAssess.CMR_ID = LMR_IDLink_CMR left join (SELECT iO_Product_MasterReference.RMR_SeqNumber, iO_Client_MasterReference.CMR_Name AS Branch FROM M3_MAIN.dbo.iO_Client_MasterReference iO_Client_MasterReference, M3_MAIN.dbo.iO_Link_MasterReference iO_Link_MasterReference, M3_MAIN.dbo.iO_Product_MasterReference iO_Product_MasterReference WHERE iO_Link_MasterReference.LMR_IDLink_Code_ID = iO_Product_MasterReference.RMR_ID AND iO_Link_MasterReference.LMR_IDLink_CMR = iO_Client_MasterReference.CMR_ID AND (iO_Link_MasterReference.LMR_IDLink_Association='{7e504c4d-821c-4623-a928-28ee65c3b8c8}')) as BR ON br.RMR_SeqNumber  = iO_DataStorage_SalesReport.DSS_LoanNumber where XLK_Detail = 'Loan\\Assessor' and br.Branch = '[TEAMNAME]' group by CMAssess.cmr_name ORDER by fees DESC